import React, { Component } from 'react';
// import Navbar from '../components/Navbar';
import Navbar2 from '../components/Navbar2';
import Footer from '../components/Footer';
import firebase from '../config/firebase';
import { connect } from 'react-redux';
import { order_request } from '../store/action';
import { my_foods } from '../store/action';
import {sendSurvey} from '../config/firebase'


import 'bootstrap/dist/css/bootstrap.css';
import '../App.css'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

class OrderRequests extends Component {
    constructor() {
        super()
        this.state = {
            tab1: "col-12 col-lg-4 col-md-4 text-center order-req-tab-active",
            tab2: "col-12 col-lg-4 col-md-4 text-center",
            tab3: "col-12 col-lg-4 col-md-4 text-center",
            tab1Content: true,
            tab2Content: false,
            tab3Content: false,
            //Survey
            date: "",
            fever: "",
            chill: "",
            cough: "",
            diffBreathe: "",
            soreThroat: "",
            aches: "",
            diarrhea: "",
            fatigue: "",
            congestion: "",
            tasteLoss: "",
        }
        this.handleDate = this.handleDate.bind(this);
        this.handleFever = this.handleFever.bind(this);
        this.handleChill = this.handleChill.bind(this);
        this.handleCough = this.handleCough.bind(this);
        this.handleDiffBreathe = this.handleDiffBreathe.bind(this);
        this.handleSoreThroat = this.handleSoreThroat.bind(this);
        this.handleAches = this.handleAches.bind(this);
        this.handleDiarrhea = this.handleDiarrhea.bind(this);
        this.handleFatigue = this.handleFatigue.bind(this);
        this.handleCongestion = this.handleCongestion.bind(this);
        this.handleTasteLoss = this.handleTasteLoss.bind(this);
    }
    handleDate(e) {
        const date = e;
        this.setState({
            date: date
        })
    }

    handleFever(e) {
        const fever = e;
        this.setState({
            fever: fever
        });
    }

    handleChill(e) {
        const chill = e;
        this.setState({
            chill: chill
        });
    }

    handleCough(e) {
        const cough = e;
        this.setState({
            cough: cough
        });
    }

    handleDiffBreathe(e) {
        const diffBreathe = e;
        this.setState({
            diffBreathe: diffBreathe
        });
    }

    handleSoreThroat(e) {
        const soreThroat = e;
        this.setState({
            soreThroat: soreThroat
        });
    }

    handleAches(e) {
        const aches = e;
        this.setState({
            aches: aches
        });
    }

    handleDiarrhea(e) {
        const diarrhea = e;
        this.setState({
            diarrhea: diarrhea
        });
    }

    handleFatigue(e) {
        const fatigue = e;
        this.setState({
            fatigue: fatigue
        });
    }

    handleCongestion(e) {
        const congestion = e;
        this.setState({
            congestion: congestion
        });
    }

    handleTasteLoss(e) {
        const tasteLoss = e;
        this.setState({
            tasteLoss: tasteLoss
        });
    }

    async handleSubmitBtn() {
        const { date, fever, chill, cough, diffBreathe, soreThroat, aches, diarrhea, fatigue, congestion, tasteLoss } = this.state

        var symptomCount = 0;
        if (fever == "fever") {
            symptomCount = symptomCount + 1;
        }
        if (chill == "chill") {
            symptomCount = symptomCount + 1;
        }
        if (cough == "cough") {
            symptomCount = symptomCount + 1;
        }
        if (diffBreathe == "diffBreathe") {
            symptomCount = symptomCount + 1;
        }
        if (soreThroat == "soreThroat") {
            symptomCount = symptomCount + 1;
        }
        if (aches == "aches") {
            symptomCount = symptomCount + 1;
        }
        if (diarrhea == "diarrhea") {
            symptomCount = symptomCount + 1;
        }
        if (fatigue == "fatigue") {
            symptomCount = symptomCount + 1;
        }
        if (congestion == "congestion") {
            symptomCount = symptomCount + 1;
        }
        if (tasteLoss == "tasteLoss") {
            symptomCount = symptomCount + 1;
        }
        const surveyDetails = {
            date: date,
            fever: fever,
